const { resolve } = require('path')
const r = path => resolve(__dirname, path)
const jsyaml = require('js-yaml')
const fs = require('fs');
var yamlConfig = fs.readFileSync(r('cat.yml'), { encoding: 'utf8' })
var yaml = jsyaml.load(yamlConfig);

function getnav() {
  var data = []
  yaml.nav.forEach(e => {
    for (k in e) {
      data.push({
        'text': k,
        'items': function () {
          var data1 = new Array()
          e[k].forEach(f => {
            for (h in f) {
              data1.push({
                'text': h,
                'link': f[h]
              })
            }
          })
          return data1
        }()
      })
    }
  });
  return data;
}

